d rgba(#f8c54d, .8);\\r\\n  position: absolute;\\r\\n  top: 125px;\\r\\n  left: 84px;\\r\\n  transform: skew(-54deg);\\r\\n}\\r\\n\\r\\n.ring-bigger-before,\\r\\n.ring-bigger-after {\\r\\n  transform: skew(-54deg) scale(1.3);\\r\\n  border-color: rgb(211, 188, 104);\\r\\n}\\r\\n\\r\\n.ring-before,\\r\\n.ring-bigger-before {\\r\\n  z-index: 3;\\r\\n  clip-path: inset(50% 0 0 0);\\r\\n}\\r\\n\\r\\n.ring-after,\\r\\n.ring-bigger-after {\\r\\n  z-index: 1;\\r\\n  clip-path: inset(0 0 50% 0);\\r\\n}\\r\\n\\r\\n.sub-planet {\\r\\n  position: absolute;\\r\\n  top: 24%;\\r\\n  left: 75%;\\r\\n  z-index: 4;\\r\\n  width: 35px;\\r\\n  height: 35px;\\r\\n  background: linear-gradient(to bottom, $blue-light 1%, $blue 50%, $blue-dark 100%);\\r\\n  border-radius: 50%;\\r\\n  transform: rotate(-15deg);\\r\\n}\\r\\n\\r\\n.orbit {\\r\\n  width: 2px;\\r\\n  height: 2px;\\r\\n  background: white;\\r\\n  border-radius: 50%;\\r\\n  position: absolute;\\r\\n  top: 50%;\\r\\n  left: 50%;\\r\\n  transform: translate(-50%, -50%);\\r\\n  z-index: 9;\\r\\n}\\r\\n\\r\\n@for $i from 1 through 6 {\\r\\n  \\r\\n  .orbit-#{$i} {\\r\\n    animation: orbit-#{$i} 5s * $i linear infinite;\\r\\n  }\\r\\n  \\r\\n  @keyframes orbit-#{$i} {\\r\\n    from { transform: rotate(0deg) translateX(50px + 15px * $i) rotate(0deg)      }\\r\\n    to   { transform: rotate(360deg) translateX(50px + 15px * $i) rotate(-360deg) }\\r\\n  } \\r\\n}\\r\\n\\r\\n.cloud {\\r\\n  background: #f1f1f1;\\r\\n  width: 100px;\\r\\n  height: 30px;\\r\\n  position: absolute;\\r\\n  border-radius: 50%;\\r\\n  bottom: -5px;\\r\\n  box-shadow: 0 0 40px rgba(black, .1);\\r\\n  \\r\\n  &:before,\\r\\n  &:after {\\r\\n    content: \\\"\\\";\\r\\n    background: #f1f1f1;\\r\\n    border-radius: 50%;\\r\\n    position: absolute;\\r\\n  }\\r\\n  \\r\\n  &:before {\\r\\n    width: 50px;\\r\\n    height: 50px;\\r\\n    bottom: 2px;\\r\\n    left: 5px;\\r\\n  }\\r\\n  \\r\\n  &:after {\\r\\n    width: 40px;\\r\\n    height: 40px;\\r\\n    bottom: 2px;\\r\\n    right: 15px;\\r\\n  }\\r\\n}\\r\\n\\r\\n.cloud-group-1,\\r\\n.cloud-group-2,\\r\\n.cloud-group-3 {\\r\\n  position: absolute;\\r\\n  bottom: 0;\\r\\n  width: 211px;\\r\\n  height: 100px;\\r\\n  opacity: .2;\\r\\n}\\r\\n\\r\\n.cloud-group-1 {\\r\\n  bottom: -5px;\\r\\n}\\r\\n\\r\\n.cloud-group-2 {\\r\\n  transform: scale(2);\\r\\n  bottom: 20px;\\r\\n  left: -50px;\\r\\n}\\r\\n\\r\\n.cloud-group-3 {\\r\\n  transform: scale(3);\\r\\n  bottom: 100px;\\r\\n  left: -24px;\\r\\n}\\r\\n\\r\\n.cloud-2 { left: 10px }\\r\\n.cloud-3 { right: 10px }\\r\\n.cloud-4 { right: 184px; bottom: 0 }\\r\\n.cloud-5 { right: 24px; bottom: 12px }\\r\\n.cloud-6 { right: -45px; bottom: 9 }\\r\\n\\r\\n.star {\\r\\n  position: absolute;\\r\\n  left: 0;\\r\\n  top: 50px;\\r\\n  width: 1px;\\r\\n  height: 1px;\\r\\n  background-color: white;\\r\\n  animation: shooting 5s linear infinite;\\r\\n  \\r\\n  &:before {\\r\\n    content: \\\"\\\";\\r\\n    position: absolute;\\r\\n    width: 100px;\\r\\n    height: 1px;\\r\\n    right: 1px;\\r\\n    top: 0;\\r\\n    background: linear-gradient(to right, rgba(black, 0) 0%,rgba(white, .4) 100%);    \\r\\n  }\\r\\n}\\r\\n\\r\\n.star-2 { top: 100px; animation: shooting 8s 1s linear infinite }\\r\\n.star-3 { top: 250px; animation: shooting 3s 2s linear infinite }\\r\\n\\r\\n@keyframes daylight {\\r\\n  0%   { background-position: 0% 50%   }\\r\\n  50%  { background-position: 100% 50% }\\r\\n  100% { background-position: 0% 50%   }\\r\\n}\\r\\n\\r\\n@keyframes shooting {\\r\\n  from { left: -100px             }\\r\\n  to   { left: calc(100% + 100px) }\\r\\n}\\r\\n}\",\"js\":\"\\/\\/ overlay (SVG path element)\\r\\nconst overlayPath = document.querySelector(\'.overlay__path\');\\r\\n\\r\\n\\/\\/ paths\\r\\n\\/\\/ edit here: https:\\/\\/yqnn.github.io\\/svg-path-editor\\/\\r\\nconst paths = {\\r\\n    step1: {\\r\\n        unfilled: \'M 0 0 h 0 c 0 50 0 50 0 100 H 0 V 0 Z\',\\r\\n        inBetween: \'M 0 0 h 43 c -60 55 140 65 0 100 H 0 V 0 Z\',\\r\\n        \\/*\\r\\n        M 0 0 h 34 c 73 7 73 94 0 100 H 0 V 0 Z\\r\\n        M 0 0 h 33 c -30 54 113 65 0 100 H 0 V 0 Z\\r\\n        M 0 0 h 34 c 112 44 -32 49 0 100 H 0 V 0 Z\\r\\n        *\\/\\r\\n        filled: \'M 0 0 h 100 c 0 50 0 50 0 100 H 0 V 0 Z\',\\r\\n    },\\r\\n    step2: {\\r\\n        filled: \'M 100 0 H 0 c 0 50 0 50 0 100 h 100 V 50 Z\',\\r\\n        \\/\\/inBetween: \'M 100 0 H 50 c 20 33 20 67 0 100 h 50 V 0 Z\',\\r\\n        inBetween: \'M 100 0 H 50 c 28 43 4 81 0 100 h 50 V 0 Z\',\\r\\n        unfilled: \'M 100 0 H 100 c 0 50 0 50 0 100 h 0 V 0 Z\',\\r\\n    }\\r\\n};\\r\\n\\r\\n\\/\\/ landing page\\/content element \\r\\nconst landingEl = document.querySelector(\'.view--2\');\\r\\n\\r\\n\\/\\/ transition trigger button\\r\\nconst switchCtrl = document.querySelector(\'button.button--open\');\\r\\n\\r\\n\\/\\/ back button\\r\\nconst backCtrl = landingEl.querySelector(\'.button--close\');\\r\\n\\r\\nlet isAnimating = false;\\r\\n\\r\\nlet page = 1;\\r\\n\\r\\nconst pageSwitchTimeline = gsap.timeline({\\r\\n            paused: true,\\r\\n            onComplete: () => isAnimating = false\\r\\n        })\\r\\n        .set(overlayPath, {\\r\\n            attr: { d: paths.step1.unfilled }\\r\\n        })\\r\\n        .to(overlayPath, { \\r\\n            duration: 0.8,\\r\\n            ease: \'power3.in\',\\r\\n            attr: { d: paths.step1.inBetween }\\r\\n        }, 0)\\r\\n        .to(overlayPath, { \\r\\n            duration: 0.2,\\r\\n            ease: \'power1\',\\r\\n            attr: { d: paths.step1.filled },\\r\\n            onComplete: () => switchPages()\\r\\n        })\\r\\n\\r\\n        .set(overlayPath, { \\r\\n            attr: { d: paths.step2.filled }\\r\\n        })\\r\\n\\r\\n        .to(overlayPath, { \\r\\n            duration: 0.15,\\r\\n            ease: \'sine.in\',\\r\\n            attr: { d: paths.step2.inBetween }\\r\\n        })\\r\\n        .to(overlayPath, { \\r\\n            duration: 1,\\r\\n            ease: \'power4\',\\r\\n            attr: { d: paths.step2.unfilled }\\r\\n        });\\r\\n\\r\\nconst switchPages = () => {\\r\\n    if ( page === 2 )\\u00a0{\\r\\n        landingEl.classList.add(\'view--open\');\\r\\n    }\\r\\n    else {\\r\\n        landingEl.classList.remove(\'view--open\');\\r\\n    }\\r\\n}\\r\\n\\r\\n\\/\\/ reveals the second content view\\r\\nconst reveal = ()  => {\\r\\n    \\r\\n    if ( isAnimating ) return;\\r\\n    isAnimating = true;\\r\\n\\r\\n    page = 2;\\r\\n    \\r\\n    pageSwitchTimeline.play(0);\\r\\n}\\r\\n\\r\\n\\/\\/ back to first content view\\r\\nconst unreveal = ()  => {\\r\\n    \\r\\n    if ( isAnimating ) return;\\r\\n    isAnimating = true;\\r\\n\\r\\n    page = 1;\\r\\n    \\r\\n    pageSwitchTimeline.play(0);\\r\\n}\\r\\n\\r\\n\\/\\/ click on menu button\\r\\nswitchCtrl.addEventListener(\'click\', reveal);\\r\\n\\/\\/ click on close menu button\\r\\nbackCtrl.addEventListener(\'click\', unreveal);\"}', 'assets/img/57/112.png', 57, 'SVG Page Transition');
